import classNames from 'classnames';
import React from 'react'
import { Link } from 'react-router-dom'
import Header from '../components/Header';
import ListItem from '../components/ListItem';
import homeStore from '../stores/homeStore'

export default function Home() {
    const store = homeStore();

    React.useEffect(() => {
        store.fetchCoins();
    }, []);

  return (
    <div>
        <Header />
        <header className='home-search'>
            <div className='width'>
                <h2>Search Coins</h2>
                <div 
                    className={classNames("home-search-input", 
                        {searching: store.searching, 
                })}
                >
                    <input type="text" value={store.query} onChange={store.setQuery}/>
                    <svg 
                        xmlns="http://www.w3.org/2000/svg" 
                        viewBox="0 0 512 512" 
                        width="20"
                    >
                        <path 
                        fill="currentColor" 
                        d="M304 48c0-26.5-21.5-48-48-48s-48 21.5-48 48s21.5 48 48 48s48-21.5 48-48zm0 416c0-26.5-21.5-48-48-48s-48 21.5-48 48s21.5 48 48 48s48-21.5 48-48zM48 304c26.5 0 48-21.5 48-48s-21.5-48-48-48s-48 21.5-48 48s21.5 48 48 48zm464-48c0-26.5-21.5-48-48-48s-48 21.5-48 48s21.5 48 48 48s48-21.5 48-48zM142.9 437c18.7-18.7 18.7-49.1 0-67.9s-49.1-18.7-67.9 0s-18.7 49.1 0 67.9s49.1 18.7 67.9 0zm0-294.2c18.7-18.7 18.7-49.1 0-67.9S93.7 56.2 75 75s-18.7 49.1 0 67.9s49.1 18.7 67.9 0zM369.1 437c18.7 18.7 49.1 18.7 67.9 0s18.7-49.1 0-67.9s-49.1-18.7-67.9 0s-18.7 49.1 0 67.9z"/></svg>
                </div>
            </div>
        </header>
        <div className='home-cryptos'>
            <div className='width'>
                <h2>Trending Coins</h2>
                <div class="home-cryptos-list">
                    {store.coins.map((coin) => {
                        return <ListItem key={coin.id} coin={coin}/>
                    })}
                </div>
            </div>
        </div>
    </div>
  );   
}
